import React, { Component } from "react";
import ReactFamilyTree from "react-family-tree";
import nodes from "./prins.json";
import nodes2 from "./diamant.json";
import PinchZoomPan from "pinch-zoom-pan";
import FamilyNode from "./FamilyNode";
import styles from "./App.module.css";
import useDialog from "./useDialog";
import ContactUsDialog from "./ContactUsDialog";
import Footer from "./Footer";


const myID = "0";
const WIDTH = 260;
const HEIGHT = 280;
var rootId = "0"
      //  var [rootId, setRootId] = useState(myID);
class CreateFamilyTree extends Component {
       // 
       // const { Dialog, open: openDialog } = useDialog();
    //const onResetClick = React.useCallback(() => setRootId(myID), []);
    render() {

        return (
            <ContactUsDialog>
                <div className={styles.root}>
                    <div style={{ fontSize: 40, textAlign: "center" }}>
                        {"משפחת  "} {nodes[0].name}

                    </div>

                    {rootId !== myID && (
                        <button
                            onClick={() => rootId="0"}
                            style={{
                                width: 140,
                                height: 30,
                                transform: `translate(60px,-50px)`,
                                color: "black",
                                backgroundColor: "lightgray",
                                fontSize: 20
                            }}
                        >
                            חזור להתחלה
                        </button>
                    )}

                    <PinchZoomPan
                        captureWheel
                        min={0.2}
                        max={2.5}
                        className={styles.wrapper}
                        key={rootId}
                    >
                        <ReactFamilyTree
                            nodes={nodes}
                            rootId={rootId}
                            width={WIDTH}
                            height={HEIGHT}
                            canvasClassName={styles.tree}
                            renderNode={node => (
                                <FamilyNode
                                    key={node.id}
                                    node={node}
                                    isRoot={node.id === rootId}
                                    style={{
                                        width: WIDTH,
                                        height: HEIGHT,
                                        transform: `translate(${node.left *
                                            (WIDTH / 2)}px, ${node.top * (HEIGHT / 2)}px)`
                                    }}
                                />
                            )}
                        />
                    </PinchZoomPan>
                </div>
            </ContactUsDialog>
        );

    }
}
export default CreateFamilyTree;
